        public int LengthOfLongestSubstring(string s)
        {
            var map = new Dictionary<char, int>();
            int maxLength = 0;
            int startOfSubstring = 0;

            for (var idx = 0; idx < s.Length; ++idx)
            {
                var nextChar = s[idx];
                if (!map.ContainsKey(nextChar))
                {
                    map.Add(nextChar, idx);
                    continue;
                }

                var currentLength = idx - startOfSubstring;
                maxLength = Math.Max(currentLength, maxLength);

                var prevPos = map[nextChar];
                map[nextChar] = idx;
                if (prevPos >= startOfSubstring)
                {
                    startOfSubstring = prevPos + 1;
                }
            }

            var finalLength = s.Length - startOfSubstring;
            maxLength = Math.Max(finalLength, maxLength);

            return maxLength;
        }

        public int LengthOfLongestSubstringByBuildingHashSet(string s)
        {
            HashSet<char> charsFound = new HashSet<char>();
            int maxLength = 0;

            for(var idx=0; idx < s.Length; ++idx)
            {
                charsFound.Clear();

                for (var charIdx = idx; charIdx < s.Length; ++charIdx)
                {
                    var nextChar = s[charIdx];
                    if (!charsFound.Contains(nextChar))
                    {
                        charsFound.Add(nextChar);
                        continue;
                    }

                    break;
                }

                //maxLength = Math.Max(charsFound.Count, maxLength);
                if (charsFound.Count > maxLength) {
                    maxLength = charsFound.Count;
                }
            }

            return maxLength;
        }
